import React, { Component } from "react";
import API from "../utils/API";
import Container from "../components/Container";
import SearchForm from "../components/SearchForm";
import SearchResults from "../components/SearchResults";
import Alert from "../components/Alert";

class Search extends Component {
  state = {
    search: "",
    results: [],
    error: "",
    alphabetical: "az",
  };

  //   When the component mounts, get a list of employees
  componentDidMount() {
    API.getEmployees()
      .then((res) => this.setState({ results: res.data.results }))
      .catch((err) => console.log(err));
  }

  handleInputChange = (event) => {
    event.preventDefault();

    this.setState({ search: event.target.value });
  };

  handleFormSubmit = (event) => {
    event.preventDefault();

    API.getEmployees(this.state.search)

      .then((res) => {
        if (res.data.status === "error") {
          throw new Error(res.data.message);
        }
        console.log(res.data);
        // this.setState({ results: res.data.results });
        //     if (!this.state.search)
        //     {

        this.setState({ results: this.state.results });
        //   }
        // if (this.state.search)
        // {
        //       const response = this.state.results.filter(u =>
        //          u.name.first.startsWith(this.state.search)
        //        );
        //    this.setState({ results: filteredUsers });
        //  }
      })

      .catch((err) => this.setState({ error: err.message }));
  };
  render() {
    // const data = this.state.results

    // console.log(emp)
    return (
      <div>
        <Container style={{ minHeight: "80%" }}>
          <h1 className="text-center">Search By Employee Name!</h1>
          <Alert
            type="danger"
            style={{ opacity: this.state.error ? 1 : 0, marginBottom: 10 }}
          >
            {this.state.error}
          </Alert>
          <SearchForm
            search={this.state.search}
            handleInputChange={this.handleInputChange}
            handleFormSubmit={this.handleFormSubmit}
            // handleInputChange={this.handleInputChange}
            // names={this.state.names}
          />
          <table className="table">
            <thead>
              <tr>
                <th scope="col">Image</th>
                <th scope="col">First Name</th>
                <th scope="col">Last Name</th>
                <th scope="col">Email</th>
                <th scope="col">Phone</th>
                <th scope="col">DOB</th>
              </tr>
            </thead>
            {this.state.results.filter((results) => {
                if(!this.state.search) {
                    return results
                  } 
                const filteredNames = results.name.first.includes(
                  this.state.search
                );
                return filteredNames;
              })
              .map((result) => (
                <SearchResults
                  first={result.name.first}
                  last={result.name.last}
                  email={result.email}
                  image={result.picture.large}
                  phone={result.cell}
                  dob={result.dob.date}
                />
              ))}
          </table>
        </Container>
      </div>
    );
  }
}

export default Search;
